const r = require('@ebay/retriever');
const renameKeys = (keysMap, obj) =>
    Object.keys(keysMap).reduce((acc, key) => {
        //console.log(keysMap[key]);
        // console.log(r.get(obj, keysMap[key], ''));
        //r.get(obj, keysMap[key], '');
        console.log(obj[key]);
        const value = r.get(obj, keysMap[key], '');
        const renamed = {
            [key]: value
        };
        console.log(keysMap[key].split('.')[0]);
        delete obj[keysMap[key].split('.')[0]];
        console.log(renamed);
        return {
            ...acc,
            ...renamed,
            ...obj
        }
    }, {});
const obj = { last: { container: 'prathap' }, name: "ajay", job: 'Front-End Master', shoeSize: 100 };
const renameObj = renameKeys({ items: 'last.container', title: "name" }, obj);

console.log(obj);
console.log(renameObj);
